export const ADD_CHARACTER = 'ADD_CHARACTER'
export const ADD_FAVORITE = 'ADD_FAVORITE';
export const REMOVE_FAVORITE ='REMOVE_FAVORITE';
export const DELETE_CHARAPTERS = 'DELETE_CHARAPTERS'
export const GET_CHARAPTERS_DETAILS = 'GET_CHARAPTERS_DETAILS'
export const GET_ALL_CHARACTERS = 'GET_ALL_CHARACTERS'


export const getAllCharacters = ()=>{
    return { type: GET_ALL_CHARACTERS}
}

export const getCharacter = (characterId) =>{
    return async (dispatch) => {

        await fetch(`https://rickandmortyapi.com/api/character/${characterId}`)
        .then((response) => response.json())
        .then((data) => {
            if (data.name)  {
                return dispatch({ type: ADD_CHARACTER, payload: data });
            }else {
                window.alert('No hay personajes con ese ID');
            }
        })
    }
}

export const getCharacterDetail = (id) => { 
    return async (dispatch) => {
        // const response = await fetch(`https://rickandmortyapi.com/api/character/${id}`);
        // const data = await response.json();
        // return dispatch({ type: GET_CHARAPTERS_DETAILS, payload: data });

        fetch(`https://rickandmortyapi.com/api/character/${id}`)
          .then((response) => response.json())
          .then((char) => {
            if (char.name) {
              //setCharacter(char);
              return dispatch({ type: GET_CHARAPTERS_DETAILS, payload: char });
            } else {
              window.alert("No hay personajes con ese ID");
            }
          })
          .catch((err) => {
            window.alert("No hay personajes con ese ID");
          });




      };
};

export const addFavorite = (character)=>{
    return {
        type: ADD_FAVORITE,
        payload: character
    }
}

export const removeFavorite = (id) => {
    return {
        type: REMOVE_FAVORITE,
        payload: id 
    }
}

export const deleteCharacter = (id) => {
    return {
        type: DELETE_CHARAPTERS,
        payload: id
    }
};
