import {
    ADD_CHARACTER,
    GET_ALL_CHARACTERS,
    ADD_FAVORITE,
    REMOVE_FAVORITE,
    DELETE_CHARAPTERS,
    GET_CHARAPTERS_DETAILS
} from './action'


const initialState = {
    characters: [],
    myFavorites: []
 };

 const rootReducer = (state = initialState, {type, payload}) => {  

    switch (type) {

        case ADD_CHARACTER:{

            const encontrado = state.characters.find(personaje => personaje.id === Number(payload.id));
            console.log("en reducer: ", payload);
            if (!encontrado) {

                return {
                    ...state,
                    characters: [...state, payload]
                }
            } else {
                window.alert('El ID ya se encuentra seleccionado');
            }
            break;
        }

        case GET_ALL_CHARACTERS: {
            return {
                ...state,
                characters: state.charapters
            }
        }



        case ADD_FAVORITE:{

            return {
                ...state,
                myFavorites: payload
            };
        }
        case REMOVE_FAVORITE:

            return {
                ...state,
                myFavorites: payload.id
            };
        default:
            return { 
                ...state
            };
    }


}; 

export default rootReducer;
