export const validate = (inputs) =>{
    const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
    const regexPass = /[0-9]*/;
    const errors = {};
    if (!inputs.username) {
      errors.username = 'El usuario no puede estar vacío';
    }
    if (!regexEmail.test(inputs.username)) {
        errors.username = 'Debe ser un correo electrónico válido';
    }
    if(inputs.username.lenght > 35){
        errors.username = 'No puede contener más de 35 caracteres';
    }
    if (!inputs.password) {
      errors.message = 'La contraseña no puede estar vacía';
    }

    if (inputs.password.lenght > 6 && inputs.password.lenght < 10) {
        errors.message = 'Longitud de la contraseña inválida';
    }

    if (!regexPass.test(inputs.password)){
        errors.message = 'La contraseña debe contener al menos 1 numero';
    }

    return errors;
  }
