import { Route, Routes } from 'react-router-dom';
import './App.css'
import Cards from './components/Cards.jsx'
import Nav from './components/Nav.jsx'
import { useState } from'react';
import ROUTES from './helpers/routes.helpers';
import About from './components/About';

function App () {

  const [characters, setCharacters] = useState([]);

  function onSearch(character) {
    console.log(Number(character));
    const encontrado = characters.find(personaje => personaje.id === Number(character));

    if (!encontrado) {
    fetch(`https://rickandmortyapi.com/api/character/${character}`)
       .then((response) => response.json())
       .then((data) => {
        if (data.name) {
             setCharacters((oldChars) => [...oldChars, data]);
          } else {
             window.alert('No hay personajes con ese ID');
          }
       });
      } else {
        window.alert('El ID ya se encuentra seleccionado');
      }
 }

 const onClose = id => {
    setCharacters(characters.filter(character => character.id !== id));
    console.log(id);
 }

  return (
    <div className='App'>

      <Nav onSearch={onSearch} />

      <Routes>
        <Route path={ROUTES.HOME} element={<Cards characters={characters} onClose={onClose} />} />
        <Route path={ROUTES.HOME} element={<About />} />
      </Routes>

      <hr />

    </div>
  )
}

export default App
