import ROUTES from '..//helpers/routes.helper';
import { Link } from 'react-router-dom';
import styles from './Card.module.css'
import { useDispatch } from 'react-redux';
import { deleteCharacter } from '../redux/action'

export default function Card(props) {

   const dispatch = useDispatch();

   const handleOnClick = () => dispatch(deleteCharacter(props.id));

   const handleOnFavorite = (event) => {
      const { favorite } = event.target.value;
      favorite ? console.log(true) : console.log(false)
   };

   return (
      <div className={styles.card}>
         <div className={styles.encabezado}>
            <button onClick={handleOnFavorite}>favorite</button>
            <span>#{props.id}</span>
            <button onClick={handleOnClick}>X</button>
         </div>
         <img  src={props.image} alt="" />
         <Link to={ROUTES.DETAIL+props.id} >
             <h4 className="card-title">{props.name}</h4>
         </Link>
         <div className={styles.grilla}>
         <p className={styles.caracteristicas}>{props.species}</p>
         <p className={styles.caracteristicas}>{props.gender}</p>
         </div>
      </div>
   );
}
