import {
    ADD_CHARACTER,
    ADD_FAVORITE,
    REMOVE_FAVORITE,
    DELETE_CHARAPTERS,
    GET_CHARAPTERS_DETAILS,
    GET_ALL_CHARACTERS
} from './action'


const initialState = {
    characters: [],
    myFavorites: []
 };

 const rootReducer = (state = initialState, {type, payload}) => {  

    switch (type) {

        case ADD_CHARACTER:{

            console.log("en reducer: ", payload);

            return {
                  ...state,
                  characters: [...state, payload]
            }
        }

        case GET_ALL_CHARACTERS:{

            console.log("en reducer: ", state.characters) ;

            return {
                ...state.characters                  
            }
        }

        case GET_CHARAPTERS_DETAILS: {
            return {
                ...state,
                characters: state.charapters
            }
        }

        case DELETE_CHARAPTERS: {
            return;
        }

        case ADD_FAVORITE:{

            return {
                ...state,
                myFavorites: payload
            };
        }
        case REMOVE_FAVORITE:

            return {
                ...state,
                myFavorites: payload.id
            };
        default:
            return { 
                ...state
            };
    }


}; 

export default rootReducer;
