import { Form } from "react-router-dom";
import {
    GET_CHARACTER,
    GET_ALL_CHARACTERS,
    ADD_FAVORITE,
    REMOVE_FAVORITE,
    DELETE_CHARAPTERS,
    GET_CHARAPTERS_DETAILS
} from './action'


const initialState = {
    charapters: [],
    myFavorites: []
 };

 const rootReducer = (state = initialState, {type, payload}) => {  

    switch (type) {

        case GET_CHARACTER:{
            return {
                ...state,
                charapters: [...state, payload]
            }
        }

        case GET_ALL_CHARACTERS: {
            return {
                ...state,
                charapters: state.charapters
            }
        }



        case ADD_FAVORITE:{

            return {
                ...state,
                myFavorites: payload
            };
        }
        case REMOVE_FAVORITE:

            return {
                ...state,
                myFavorites: payload.id
            };
        default:
            return { 
                ...state
            };
    }


}; 

export default rootReducer;
