import { useSelector } from "react-redux"
import Card from './Card';

const Favorites = () => {


    const myFavorites = useSelector((State)=> State.myFavorites)
    return (
        <div><h1>Mis Favoritos</h1>
            {
                myFavorites.map( fav => {
                    return <Card
                        key={fav.id}
                        name={fav.name} 
                        image={fav.image}
                    />
                })
            }
        </div>
    )
}

export default Favorites
