import {
    ADD_CHARACTER,
    FAVORITE,
    DELETE_CHARAPTERS,
} from './action'


const initialState = {
    characters: [],
    myFavorites: []
 };

 const rootReducer = (state = initialState, {type, payload}) => {  

    switch (type) {

        case ADD_CHARACTER:{
            return {
                  ...state,
                  characters: [ ...state.characters, payload]
            }
        }

        case DELETE_CHARAPTERS: {

            return {
                ...state,
                characters: state.characters.filter(character => character.id !== payload)
            };
        }

        case FAVORITE:

            return {
                ...state,
                myFavorites: [...state.myFavorites, payload.id]
            };
        default:
            return { 
                ...state
            };
    }


}; 

export default rootReducer;
