  const [characters, setCharacters] = useState('');

  const onSearch = (character) => {




    setCharacters([characters, ...character]);
  }

  return (
    <div className='App' style={{ padding: '25px' }}>
      <div className='navBar'>
        <SearchBar
          onSearch={onSearch}
        />
      </div>
      <div>
        <Cards
          characters={characters}
        />
      </div>
      <hr />

    </div>
  )
}

export default App
